mapper.insert:
INSERT
  INTO MARKETING
(
   MK_NAME
 , MK_START_DATE
 , MK_END_DATE
 , MK_PLACE_NAME
 , MK_PRODUCT
 , MK_ORI_MAIN
 , MK_FILE_MAIN
 , COMPANY_NAME
)
VALUES
(
   #{ mkName }
 , #{ mkStartDate }
 , #{ mkEndDate }
 , #{ mkPlaceName }
 , #{ mkProduct }
 , #{ mkOriMain }
 , #{ mkFileMain }
 , #{ companyName }
)

mapper.update:
UPDATE MARKETING
   SET MK_NAME = #{ mkName }
     , MK_START_DATE = DATE_FORMAT(#{ mkStartDate }, '%Y%m%d')
     , MK_END_DATE = DATE_FORMAT(#{ mkEndDate }, '%Y%m%d')
     , MK_PLACE_NAME = #{ mkPlaceName }
     , MK_PRODUCT = #{ mkProduct }
     , MK_ORI_MAIN = #{ mkOriMain }
     , MK_FILE_MAIN = #{ mkFileMain }
     , COMPANY_NAME = #{ companyName }
 WHERE MK_NUM = #{ mkNum }

UPDATE MARKETING
SET MK_NAME = #{ mkName }
  , MK_START_DATE = DATE_FORMAT(#{ mkStartDate }, '%Y%m%d')
  , MK_END_DATE = DATE_FORMAT(#{ mkEndDate }, '%Y%m%d')
  , MK_PLACE_NAME = #{ mkPlaceName }
  , MK_PRODUCT = #{ mkProduct }
  , COMPANY_NAME = #{ companyName }
WHERE MK_NUM = #{ mkNum }

mapper.delete:
DELETE
  FROM MARKETING
 WHERE MK_NUM = #{ mkNum }

mapper.update:
UPDATE MK_POSTER
   SET P_ORI_NAME = #{ pOriName }
     , P_FILE_NAME = #{ pFileName }
     , P_ACT_NAME = #{ pActName }
     , P_CAST_NAME = #{ pCastName }
 WHERE P_FILE_NUM = #{ pFileNum }

UPDATE MK_POSTER
   SET P_ACT_NAME = #{ pActName }
     , P_CAST_NAME = #{ pCastName }
 WHERE P_FILE_NUM = #{ pFileNum }

UPDATE MK_MD
   SET MD_ORI_NAME = #{ mdOriName }
     , MD_FILE_NAME = #{ mdFileName }
     , MD_NAME = #{ mdName }
     , MD_PRICE = #{ mdPrice }
 WHERE MD_FILE_NUM = #{ mdFileNum }

UPDATE MK_MD
   SET MD_NAME = #{ mdName }
     , MD_PRICE = #{ mdPrice }
 WHERE MD_FILE_NUM = #{ mdFileNum }

UPDATE MK_ITEM
   SET ITEM_ORI_NAME = #{ itemOriName }
     , ITEM_FILE_NAME = #{ itemFileName }
     , ITEM_NAME = #{ itemName }
 WHERE ITEM_FILE_NUM = #{ itemFileNum }

UPDATE MK_ITEM
   SET ITEM_NAME = #{ itemName }
 WHERE ITEM_FILE_NUM = #{ itemFileNum }

