mapper.select:
SELECT * FROM (SELECT @RNUM := @RNUM + 1 AS ROWNUM , N.NOTICE_NO , N.NOTICE_NAME , N.NOTICE_REGIST_DATE FROM NOTICE N ,(SELECT @RNUM:=0)AS R WHERE 1=1 ORDER BY N.NOTICE_NO DESC )list WHERE ROWNUM >= #{ startRow } AND
ROWNUM <= #{ endRow }
ORDER BY 1 ASC

mapper.update:
UPDATE
       NOTICE
   SET NOTICE_COUNT = (SELECT
                              C.NOTICE_COUNT
                         FROM (SELECT
                                      B.NOTICE_COUNT
                                 FROM NOTICE B
                                WHERE B.NOTICE_NO = #{ noticeNo }
                              ) AS C
                      ) +1
WHERE NOTICE_NO = #{ noticeNo }

mapper.insert:
INSERT
INTO NOTICE_FILE
(
  NOTICE_FILE_NUM
, NOTICE_ORI_NAME
, NOTICE_FILE_NAME
, SAVED_PATH
, NOTICE_NO
)
VALUES
(
  NOTICE_FILE_NUM
, #{ noticeOriName }
, #{ noticeFileName }
, #{ savedPath }
, #{ noticeNo }
)

    SELECT LAST_INSERT_ID()

mapper.update:
UPDATE
       NOTICE


        , NOTICE_NAME = #{ noticeName }


        , NOTICE_CONTENT = #{ noticeContent }


        , NOTICE_LINK = #{ noticeLink }

    , NOTICE_MODIFY_DATE = DATE_FORMAT(now(), '%Y%m%d%H%i%s')

WHERE NOTICE_NO = #{ noticeNo }

mapper.delete:
DELETE
  FROM NOTICE_FILE
 WHERE NOTICE_NO = #{ noticeNo }

