mapper.update:
UPDATE PRODUCT_DETAIL
   SET DETAIL_FILE_NUM = #{ detail_file_num }
     , DETAIL_FILE_NAME = #{ detail_file_name }
 WHERE DETAIL_FILE_NUM = #{ detail_file_num }

UPDATE PRODUCT_DETAIL
   SET DETAIL_FILE_NUM = #{ detail_file_num }
     , DETAIL_ORI_NAME = #{ detail_ori_name }
     , DETAIL_FILE_NAME = #{ detail_file_name }
 WHERE DETAIL_FILE_NUM = #{ detail_file_num }

mapper.insert:
INSERT
  INTO PRODUCT
  (
    PD_NAME
  , PD_START_DATE
  , PD_END_DATE
  , PD_PLACE_NAME
  , PD_RATED
  , PD_TIME
  , PD_PLACE_ADD
  , PD_ORI_MAIN
  , PD_FILE_MAIN
  , COMPANY_NAME
  )
  VALUES
  (
    #{ pd_name }
  , #{ pd_start_date }
  , #{ pd_end_date }
  , #{ pd_place_name }
  , #{ pd_rated }
  , #{ pd_time }
  , #{ pd_place_add }
  , #{ pd_Ori_Main }
  , #{ pd_File_Main }
  , #{ company_name }
  )

INSERT
  INTO PRODUCT_DETAIL
  (
     PD_NUM
   , DETAIL_ORI_NAME
   , DETAIL_FILE_NAME
  )
  VALUES
  (
    #{ pd_num }
  , #{ detail_ori_name }
  , #{ detail_file_name }
  )

mapper.update:
UPDATE PRODUCT
   SET PD_NAME = #{ pd_name }
     , PD_START_DATE = DATE_FORMAT(#{ pd_start_date }, '%Y%m%d')
     , PD_END_DATE = DATE_FORMAT(#{ pd_end_date }, '%Y%m%d')
     , PD_PLACE_NAME = #{ pd_place_name }
     , PD_RATED = #{ pd_rated }
     , PD_TIME = #{ pd_time }
     , PD_PLACE_ADD = #{ pd_place_add }
     , PD_ORI_MAIN = #{ pd_Ori_Main }
     , PD_FILE_MAIN = #{ pd_File_Main }
     , COMPANY_NAME = #{ company_name }
 WHERE PD_NUM = #{ pd_num }

UPDATE PRODUCT
   SET PD_NAME = #{ pd_name }
     , PD_START_DATE = DATE_FORMAT(#{ pd_start_date }, '%Y%m%d')
     , PD_END_DATE = DATE_FORMAT(#{ pd_end_date }, '%Y%m%d')
     , PD_PLACE_NAME = #{ pd_place_name }
     , PD_RATED = #{ pd_rated }
     , PD_TIME = #{ pd_time }
     , PD_PLACE_ADD = #{ pd_place_add }
     , COMPANY_NAME = #{ company_name }
 WHERE PD_NUM = #{ pd_num }

mapper.delete:
DELETE
  FROM PRODUCT
 WHERE PD_NUM = #{ pd_num }

DELETE
  FROM PRODUCT_DETAIL
 WHERE DETAIL_FILE_NUM = #{ detail_file_num }
